# url = 'http://192.168.33.158:3002/v1/cost/over-time'
url = 'http://qcpricingapi-duuheqjwr2lgq.lbb7lk8hq4v71.hms-dev.cloud/v1/cost/over-time'

headers = { 'authorization': token }
params = {
    'startDate': '2019-09-06T12:00',
    'endDate': '2019-09-06T14:00',
    'interval': 'PT5M',
    'tenantId': 'autogenerated',
}
response = requests.get(url, headers=headers, params=params)
print(response)
response_json = response.json()

costs = pd.DataFrame(response_json['costs'])
costs.index = pd.to_datetime(costs['periodStart'])
costs['discount'] = costs['totalCostWithoutDiscounts'] - costs['totalCost']
display(costs)
costs[['totalCost', 'discount']].plot(kind='bar', stacked=True)

